package com.protocol7.nettyquick.tls;

import com.protocol7.nettyquick.utils.Hex;
import org.junit.Assert;
import org.junit.BeforeClass;
import org.junit.Test;

import java.io.IOException;
import java.security.GeneralSecurityException;
import java.security.PublicKey;
import java.security.cert.CertificateException;
import java.security.interfaces.RSAPrivateKey;

public class CertificateVerifyTest {

    @BeforeClass
    public static void setUp() {
        java.security.Security.addProvider(new org.bouncycastle.jce.provider.BouncyCastleProvider());
    }

    @Test
    public void roundtrip() throws IOException, GeneralSecurityException {
        byte[] hash = Hex.dehex("3e66361ada42c7cb97f9a62b00cae1d8b584174c745f9a338cf9f7cdd51d15f8");

        RSAPrivateKey privateKey = KeyUtil.getPrivateKeyFromPem("src/test/resources/server.key");
        byte[] actual = CertificateVerify.sign(hash, privateKey, false);

        PublicKey publicKey = KeyUtil.getCertFromCrt("src/test/resources/server.crt").getPublicKey();

        Assert.assertTrue(CertificateVerify.verify(actual, hash, publicKey, false));
    }

    @Test
    public void verifyKnown() throws IOException, CertificateException {
        // from quic-go, including a copy of the certificate for this test
        byte[] sign = Hex.dehex("");
        byte[] hash = Hex.dehex("173322914473dfc8651a3f549eb7118a706be48f305aa738429d9af647f7722b");

        PublicKey publicKey = KeyUtil.getPublicKeyFromPem("src/test/resources/quic-go-certs.pem");

        Assert.assertTrue(CertificateVerify.verify(sign, hash, publicKey, false));
    }

    @Test
    public void name() {
        byte[] hash = Hex.dehex("d486314368c4dfd555e7cb6682abfabf3cd036799b7e5fb53c409348dd92b186");
        byte[] content = Hex.dehex("");

        System.out.println("d486314368c4dfd555e7cb6682abfabf3cd036799b7e5fb53c409348dd92b186");
        System.out.println(Hex.hex(Hash.sha256(content)));

        // ""

    }
}